<template>
	<ul class="tab-container">
		<li
			v-for="(item, index) in tabList"
			:key="index"
			class="tab-item"
			@click="jumpTo(item.link, index)"
		>
			<img
				class="img"
				:src="index === activeIndex ? item.activeUrl : item.defaultUrl"
				alt=""
			/>
			<div :class="[index === activeIndex ? 'active' : '', 'name']">
				{{ item.name }}
			</div>
		</li>
	</ul>
</template>

<script>
export default {
	name: 'FootTab',
	data() {
		return {
			tabList: [
				{
					name: '电影',
					link: '/movie',
					defaultUrl:
						'data:image/png;base64,',
					activeUrl:
						'data:image/png;base64,'
				},
				{
					name: '影院',
					link: '/cinema',
					defaultUrl:
						'data:image/png;base64,',
					activeUrl:
						'data:image/png;base64,'
				},
				{
					name: '我的',
					link: 'my',
					defaultUrl:
						'data:image/png;base64,',
					activeUrl:
						'data:image/png;base64,'
				}
			],
			activeIndex: 0
		};
	},
	methods: {
		jumpTo(link, index) {
			this.activeIndex = index;
			this.$router.push(link);
		}
	}
};
</script>

